mapper.update:
UPDATE book


        book_name = #{bookName}


        author = #{author}


        image_url = #{imageUrl}


        location = #{location}


        borrow_date = #{borrowDate}


        return_date = #{returnDate}


        type_id = #{typeId}


        isbn = #{isbn}


WHERE book_id = #{bookId}

mapper.select:
SELECT b.*, r.*,u.nickname FROM book b, user_book r, user u
WHERE b.book_id = r.book_id
AND r.user_id = u.user_id
AND ${searchName} LIKE #{search}
ORDER BY ${sort} ${order} LIMIT #{offset},#{limit}

SELECT COUNT(*) FROM book b, user_book r, user u
WHERE b.book_id = r.book_id
AND r.user_id = u.user_id
AND ${searchName} LIKE #{search}
ORDER BY ${sort} ${order} LIMIT #{offset},#{limit}
